SELECT t2.id, t2.name, t2.idcard_no, t1.maker_net_income, t1.maker_tax_fee, t1.audit_fee, t1.pay_fee, t1.total_fee, t1.service_rate, mi.maker_voice_url,
mt.maker_tax_url FROM diyi_pay_maker t1 INNER JOIN diyi_maker t2 ON t1.maker_id = t2.id LEFT JOIN diyi_maker_invoice mi ON mi.pay_maker_id = t1.id
LEFT JOIN diyi_maker_tax_record mt ON mt.pay_maker_id = t1.id AND t2.bool_deleted = 0 WHERE t1.bool_deleted = 0 AND t1.pay_enterprise_id = #{param1}

SELECT IFNULL(SUM(IF(MONTH(t1.create_time) = 1, t1.pay_to_platform_amount, 0)), 0) AS jan_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 2, t1.pay_to_platform_amount, 0)), 0) AS feb_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 3, t1.pay_to_platform_amount, 0)), 0) AS mar_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 4, t1.pay_to_platform_amount, 0)), 0) AS apr_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 5, t1.pay_to_platform_amount, 0)), 0) AS may_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 6, t1.pay_to_platform_amount, 0)), 0) AS jun_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 7, t1.pay_to_platform_amount, 0)), 0) AS jul_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 8, t1.pay_to_platform_amount, 0)), 0) AS aug_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 9, t1.pay_to_platform_amount, 0)), 0) AS sep_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 10, t1.pay_to_platform_amount, 0)), 0) AS oct_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 11, t1.pay_to_platform_amount, 0)), 0) AS nov_income, IFNULL(SUM(IF(MONTH(t1.create_time) = 12, t1.pay_to_platform_amount, 0)), 0) AS dec_income FROM diyi_pay_enterprise t1
INNER JOIN diyi_rel_bureau_service_provider t2 ON t1.service_provider_id = t2.service_provider_id AND t2.bool_deleted = 0 AND t2.rel_bureau_id = #{param2}
WHERE t1.bool_deleted = 0 AND t1.audit_state = 'APPROVED' AND YEAR(t1.create_time) = YEAR(NOW())
AND t1.service_provider_id = #{param1}

SELECT IFNULL(SUM(IF(DAY(t1.create_time) = 1, t1.pay_to_platform_amount, 0)), 0) AS one_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 2, t1.pay_to_platform_amount, 0)), 0) AS two_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 3, t1.pay_to_platform_amount, 0)), 0) AS three_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 4, t1.pay_to_platform_amount, 0)), 0) AS four_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 5, t1.pay_to_platform_amount, 0)), 0) AS five_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 6, t1.pay_to_platform_amount, 0)), 0) AS six_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 7, t1.pay_to_platform_amount, 0)), 0) AS seven_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 8, t1.pay_to_platform_amount, 0)), 0) AS eight_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 9, t1.pay_to_platform_amount, 0)), 0) AS nine_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 10, t1.pay_to_platform_amount, 0)), 0) AS ten_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 11, t1.pay_to_platform_amount, 0)), 0) AS eleven_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 12, t1.pay_to_platform_amount, 0)), 0) AS twelve_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 13, t1.pay_to_platform_amount, 0)), 0) AS thirteen_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 14, t1.pay_to_platform_amount, 0)), 0) AS fourteen_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 15, t1.pay_to_platform_amount, 0)), 0) AS fifteen_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 16, t1.pay_to_platform_amount, 0)), 0) AS sixteen_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 17, t1.pay_to_platform_amount, 0)), 0) AS seventeen_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 18, t1.pay_to_platform_amount, 0)), 0) AS eighteen_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 19, t1.pay_to_platform_amount, 0)), 0) AS nineteen_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 20, t1.pay_to_platform_amount, 0)), 0) AS twenty_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 21, t1.pay_to_platform_amount, 0)), 0) AS twenty_one_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 22, t1.pay_to_platform_amount, 0)), 0) AS twenty_two_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 23, t1.pay_to_platform_amount, 0)), 0) AS twenty_three_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 24, t1.pay_to_platform_amount, 0)), 0) AS twenty_four_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 25, t1.pay_to_platform_amount, 0)), 0) AS twenty_five_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 26, t1.pay_to_platform_amount, 0)), 0) AS twenty_six_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 27, t1.pay_to_platform_amount, 0)), 0) AS twenty_seven_day_income, IFNULL(SUM(IF(DAY(t1.create_time) = 28, t1.pay_to_platform_amount, 0)), 0) AS twenty_eight_day_income, IF(DAY(LAST_DAY(NOW())) >= 29, IFNULL(SUM(IF(DAY(t1.create_time) = 29, t1.pay_to_platform_amount, 0)), 0), NULL) AS twenty_nine_day_income, IF(DAY(LAST_DAY(NOW())) >= 30, IFNULL(SUM(IF(DAY(t1.create_time) = 30, t1.pay_to_platform_amount, 0)), 0), NULL) AS thirty_day_income, IF(DAY(LAST_DAY(NOW())) >= 31, IFNULL(SUM(IF(DAY(t1.create_time) = 31, t1.pay_to_platform_amount, 0)), 0), NULL) AS thirty_one_day_income FROM diyi_pay_enterprise t1
INNER JOIN diyi_rel_bureau_service_provider t2 ON t1.service_provider_id = t2.service_provider_id AND t2.bool_deleted = 0 AND t2.rel_bureau_id = #{param2}
WHERE t1.bool_deleted = 0 AND t1.audit_state = 'APPROVED' AND YEAR(t1.create_time) = YEAR(NOW()) AND MONTH(t1.create_time) = MONTH(NOW())
AND t1.service_provider_id = #{param1}

SELECT IFNULL(SUM(IF(WEEKDAY(t1.create_time) = 0, t1.pay_to_platform_amount, 0)), 0) AS mon_income, IFNULL(SUM(IF(WEEKDAY(t1.create_time) = 1, t1.pay_to_platform_amount, 0)), 0) AS tue_income, IFNULL(SUM(IF(WEEKDAY(t1.create_time) = 2, t1.pay_to_platform_amount, 0)), 0) AS wed_income, IFNULL(SUM(IF(WEEKDAY(t1.create_time) = 3, t1.pay_to_platform_amount, 0)), 0) AS thu_income, IFNULL(SUM(IF(WEEKDAY(t1.create_time) = 4, t1.pay_to_platform_amount, 0)), 0) AS fri_income, IFNULL(SUM(IF(WEEKDAY(t1.create_time) = 5, t1.pay_to_platform_amount, 0)), 0) AS sat_income, IFNULL(SUM(IF(WEEKDAY(t1.create_time) = 6, t1.pay_to_platform_amount, 0)), 0) AS sun_income FROM diyi_pay_enterprise t1
INNER JOIN diyi_rel_bureau_service_provider t2 ON t1.service_provider_id = t2.service_provider_id AND t2.bool_deleted = 0 AND t2.rel_bureau_id = #{param2}
WHERE t1.bool_deleted = 0 AND t1.audit_state = 'APPROVED' AND WEEK(t1.create_time) = WEEK(NOW())
AND t1.service_provider_id = #{param1}

