SELECT t1.id AS pay_enterprise_id, t2.enterprise_name, t3.service_provider_name, t1.charge_list_url, t4.worksheet_no, t1.create_time, t1.pay_to_platform_amount, t1.pay_state, t1.audit_state, t1.company_invoice_state, (SELECT GROUP_CONCAT(enterprise_pay_receipt_url) FROM diyi_pay_enterprise_receipt WHERE is_deleted = 0 AND pay_enterprise_id = t1.id) AS enterprise_pay_receipt_urls, (SELECT GROUP_CONCAT(accept_paysheet_url) FROM diyi_accept_paysheet WHERE is_deleted = 0 AND pay_enterprise_id = t1.id) AS accept_paysheet_urls FROM diyi_pay_enterprise t1 INNER JOIN diyi_enterprise t2 ON t1.enterprise_id = t2.id INNER JOIN diyi_service_provider t3 ON t1.service_provider_id = t3.id LEFT JOIN diyi_worksheet t4 ON t1.worksheet_id = t4.id AND t4.is_deleted = 0 WHERE t1.is_deleted = 0 AND t2.is_deleted = 0 AND t3.is_deleted = 0
AND t1.enterprise_id = #{param1}
AND t1.service_provider_id = #{param2} AND t1.pay_state <> 'TOPAY' AND t1.audit_state <> 'EDITING'
AND t1.id = #{param3.payEnterpriseId}
AND t1.pay_enterprise_audit_state = #{param3.payEnterpriseAuditState}
AND t3.service_provider_name LIKE concat('%', #{param3.serviceProviderName}, '%')
AND t2.enterprise_name LIKE concat('%', #{param3.enterpriseName}, '%')
AND DATEDIFF(t1.create_time, #{param3.beginDate}) >= 0
AND DATEDIFF(t1.create_time, #{param3.endDate}) <= 0

