SELECT * FROM ( SELECT a.id AS invoice_application_pay_list_id, b.id AS invoice_application_id, 0 AS platform_invoice_pay_list_id, 0 AS invoice_print_id, d.enterprise_name, e.service_provider_name, b.application_state, 0 AS company_invoice_state, b.create_time, 1 AS apply_state FROM diyi_INVOICE_APPLICATION_PAY_LIST a INNER JOIN diyi_INVOICE_APPLICATION b ON b.id = a.application_id INNER JOIN diyi_pay_enterprise c ON c.id = a.pay_enterprise_id INNER JOIN diyi_enterprise d ON d.id = c.enterprise_id INNER JOIN diyi_service_provider e ON e.id = c.service_provider_id WHERE d.id = #{param1}
GROUP BY b.create_time UNION ALL SELECT a.id AS platform_invoice_pay_list_id, b.id AS invoice_print_id, 0 AS invoice_application_pay_list_id, 0 AS invoice_application_id, d.enterprise_name, e.service_provider_name, c.company_invoice_state, 0 AS application_state, b.create_time, 0 AS apply_state FROM diyi_platform_invoice_pay_list a INNER JOIN diyi_platform_invoice b ON a.invoice_print_id = b.id INNER JOIN diyi_pay_enterprise c ON c.id = a.pay_enterprise_id INNER JOIN diyi_enterprise d ON d.id = c.enterprise_id INNER JOIN diyi_service_provider e ON e.id = c.service_provider_id WHERE d.id = #{param1}
GROUP BY b.create_time ) AS tmp where 1=1




    AND service_provider_name like concat('%', #{param2}, '%')
