SELECT e.id AS enterprise_id, si.id AS self_help_invoice_id, sa.id AS self_help_invoice_apply_id, ss.id AS self_help_invoice_apply_provider_id, ssd.id AS provider_self_help_invoice_id, sd.id AS self_help_invoice_detail_id, sp.id AS service_provider_id, e.enterprise_name, sp.service_provider_name, si.List_file, ssd.invoice_scan_pictures, ssd.tax_scan_pictures, sd.invoice_print_state, k.express_no AS express_sheet_no, k.express_company_name,ssd.create_time as update_datetime FROM diyi_self_help_invoice si INNER JOIN diyi_self_help_invoice_apply sa ON si.id = sa.self_help_invoice_id INNER JOIN diyi_self_help_invoice_detail sd ON sd.self_help_invoice_id = si.id INNER JOIN diyi_self_help_invoice_sp ss ON si.id = ss.self_help_invoice_id INNER JOIN diyi_self_help_invoice_sp_detail ssd ON sd.id = ssd.self_help_invoice_detail_id INNER JOIN diyi_enterprise e ON e.id = si.enterprise_id INNER JOIN diyi_service_provider sp ON sp.id = ss.service_provider_id LEFT JOIN diyi_self_help_invoice_express k ON k.self_help_invoice_apply_provider_id = ss.id WHERE si.is_deleted = 0 AND sa.is_deleted = 0 AND sd.is_deleted = 0 AND ss.is_deleted = 0 AND ssd.is_deleted = 0 AND e.is_deleted = 0 and ss.apply_state =#{param5}

    AND ss.service_provider_id =#{param1}


    AND e.enterprise_name like concat('%', #{param2}, '%')


    AND si.create_time > #{param3}


    AND si.create_time < #{param4}

SELECT a.id AS self_help_invoice_id, b.id AS self_help_invoice_apply_id, a.enterprise_id, c.enterprise_name, a.list_file, a.pay_type, ( SELECT SUM(charge_money_num) FROM diyi_self_help_invoice_detail v WHERE v.self_help_invoice_id = a.id ) AS total_charge_money_num, a.current_state, a.create_time FROM diyi_self_help_invoice a INNER JOIN diyi_self_help_invoice_apply b ON a.id = b.self_help_invoice_id INNER JOIN diyi_enterprise c ON c.id = a.enterprise_id WHERE b.apply_state != 'REJECTED'

    AND c.enterprise_name like concat('%', #{param1}, '%')


    AND a.create_time > #{param2}


    AND a.create_time < #{param3}

and invoice_people_type = #{param4}
