mapper.select:
SELECT zs.id AS self_help_invoice_id, rc.id AS enterprise_id, sd.id AS self_help_invoice_detail_id, a.id AS address_id, sp.id AS invoice_person_id, hf.id AS self_help_invoice_fee_id, rc.enterprise_name, sd.invoice_type, sd.charge_money_num, sd.invoice_people_name, sp.id_card_pic, sp.id_card_pic_back, sd.flow_contract_url, sd.business_contract_url, zs.current_state, zs.confirm_price_datetime FROM diyi_self_help_invoice zs INNER JOIN diyi_enterprise rc ON rc.id = zs.enterprise_id INNER JOIN diyi_self_help_invoice_detail sd ON sd.self_help_invoice_id = zs.id INNER JOIN diyi_address a ON a.id = zs.address_id INNER JOIN diyi_self_help_invoice_person sp ON sp.id = sd.none_maker_invoice_person_id INNER JOIN diyi_self_help_invoice_fee hf ON zs.id = hf.self_help_invoice_id WHERE zs.is_deleted = 0 AND rc.is_deleted = 0 AND sd.is_deleted = 0 AND a.is_deleted = 0 AND sp.is_deleted = 0 AND hf.is_deleted = 0 AND zs.apply_enterprise_id = #{param1} and zs.invoice_people_type =#{param2}
and sd.invoice_people_name like concat('%', #{param3}, '%')
and zs.create_time > #{param4}
and zs.create_time < #{param5}

SELECT t1.id, t3.enterprise_name, t1.list_file, t1.invoice_people_type, t1.charge_money_num, t2.invoice_people_name, t2.flow_contract_url, t2.business_contract_url, t2.deliver_sheet_url, t2.account_balance_url, t1.create_time FROM diyi_self_help_invoice t1 INNER JOIN diyi_self_help_invoice_detail t2 ON t1.id = t2.self_help_invoice_id INNER JOIN diyi_enterprise t3 ON t1.enterprise_id = t3.id WHERE t1.is_deleted = 0 AND t2.is_deleted = 0 AND t3.is_deleted = 0 AND t1.enterprise_id = #{param1}
AND DATEDIFF(t1.create_time, #{param2.beginDate}) >= 0
AND DATEDIFF(t1.create_time, #{param2.endDate}) <= 0
