mapper.select:
SELECT t1.id, t1.worksheet_no, t1.worksheet_name, t1.worksheet_type, t1.maker_type, t1.worksheet_mode, t2.check_money, t1.create_time,
IF(ISNULL(t2.achievement_files) || LENGTH(trim(t2.achievement_files))  <  1, FALSE, TRUE) AS bool_achievement
FROM diyi_worksheet t1 INNER JOIN diyi_worksheet_maker t2 ON t1.id = t2.worksheet_id
WHERE t1.is_deleted = 0 AND t2.is_deleted = 0

AND t1.enterprise_id = #{param1}

AND t2.maker_id = #{param2}

SELECT ws.id AS worksheet_id, ws.enterprise_id, e.enterprise_name, ws.worksheet_mode, ws.worksheet_name, ws.worksheet_type, ws.upPerson_num, ws.worksheet_no, ws.work_days, ws.worksheet_fee_low, ws.worksheet_fee_high, ws.create_time,ws.maker_type,ws.worksheet_state FROM diyi_worksheet ws INNER JOIN diyi_enterprise e ON e.id = ws.enterprise_id WHERE ws.is_deleted = 0 AND e.is_deleted = 0 AND e.id = #{param1} AND ws.worksheet_state = #{param2} and e.is_deleted = 0 and ws.is_deleted = 0
and ws.worksheet_no LIKE concat('%', #{param3}, '%')
and ws.worksheet_name LIKE concat('%', #{param4}, '%')
and ws.create_time > #{param5}
and ws.create_time < #{param6}

SELECT id, worksheet_no, worksheet_name, create_time FROM diyi_worksheet WHERE is_deleted = 0 AND worksheet_state = 'FINISHED' AND enterprise_id = #{param1} AND worksheet_type = #{param2}
AND worksheet_no LIKE concat('%', #{param3}, '%')
AND worksheet_name LIKE concat('%', #{param4}, '%')
