SELECT ep.id AS enterprise_provider_id, ep.enterprise_id, ep.service_provider_id, a.id AS agreement_id, a.agreement_no, a.agreement_url, sp.service_provider_name, a.sign_state, a.create_time FROM diyi_enterprise_service_provider ep INNER JOIN diyi_service_provider sp ON sp.id = ep.service_provider_id INNER JOIN diyi_agreement a ON sp.id = a.service_provider_id WHERE sp.bool_deleted = 0 AND a.bool_deleted = 0 AND ep.bool_deleted = 0 AND a.valid_state = 'VALIDING' AND ((a.party_a = 'SERVICEPEOPLE' AND a.party_a_id = ep.service_provider_id) OR (a.party_b = 'SERVICEPEOPLE' AND a.party_b_id = ep.service_provider_id)) AND ep.enterprise_id = #{param1} AND a.agreement_type = #{param2}
AND sp.service_provider_name LIKE concat('%', #{param3}, '%')
AND a.agreement_no LIKE concat('%', #{param4}, '%')

SELECT DISTINCT a.id AS agreement_id, m.name as maker_name, s.id AS service_provider_id, a.agreement_no, a.sign_type, a.agreement_type, s.service_provider_name, a.agreement_url, a.create_time, a.sign_state FROM diyi_agreement a INNER JOIN diyi_maker m ON a.party_b_id = m.id INNER JOIN diyi_service_provider_maker pa ON pa.maker_id = m.id INNER JOIN diyi_service_provider s ON pa.service_provider_id = s.id WHERE a.agreement_type = 'MAKERJOINAGREEMENT' AND a.valid_state = 'VALIDING' AND a.party_b = 'MAKERPEOPLE' AND pa.service_provider_id = #{param1}
and a.agreement_no LIKE concat('%', #{param2}, '%')
and m.name LIKE concat('%', #{param3}, '%')

SELECT a.id AS agreement_id, s.id AS service_provider_id, e.enterprise_name, a.agreement_no, a.sign_type, a.agreement_type, s.service_provider_name, a.agreement_url, a.create_time, a.sign_state FROM diyi_agreement a INNER JOIN diyi_enterprise e ON a.party_b_id = e.id INNER JOIN diyi_enterprise_service_provider ep ON ep.enterprise_id = e.id INNER JOIN diyi_service_provider s ON ep.service_provider_id = s.id WHERE a.agreement_type = 'ENTERPRISEJOINAGREEMENT' AND a.valid_state = 'VALIDING' AND a.party_b = 'ENTERPRISEPEOPLE' AND a.party_a = 'SERVICEPEOPLE' AND a.party_a_id = s.id AND s.id = #{param1}
and a.agreement_no LIKE concat('%', #{param2}, '%')
and e.enterprise_name LIKE concat('%', #{param3}, '%')

SELECT a.id AS agreement_id, s.id AS service_provider_id, e.enterprise_name, a.agreement_no, a.sign_type, a.agreement_type, s.service_provider_name, a.agreement_url, a.create_time, a.sign_state FROM diyi_agreement a INNER JOIN diyi_enterprise e ON a.party_b_id = e.id INNER JOIN diyi_enterprise_service_provider ep ON ep.enterprise_id = e.id INNER JOIN diyi_service_provider s ON ep.service_provider_id = s.id WHERE a.agreement_type = 'SERENTSUPPLEMENTARYAGREEMENT' AND a.valid_state = 'VALIDING' AND a.party_b = 'ENTERPRISEPEOPLE' AND a.party_a = 'SERVICEPEOPLE' AND a.party_a_id = s.id AND s.id = #{param1}
and a.agreement_no LIKE concat('%', #{param2}, '%')
and e.enterprise_name LIKE concat('%', #{param3}, '%')

SELECT a.id AS agreement_id, s.id AS service_provider_id,e.enterprise_name, a.agreement_no, a.sign_type, a.agreement_type, s.service_provider_name, a.agreement_url, a.create_time, a.sign_state FROM diyi_agreement a INNER JOIN diyi_enterprise e ON a.party_b_id = e.id INNER JOIN diyi_enterprise_service_provider ep ON ep.enterprise_id = e.id INNER JOIN diyi_service_provider s ON ep.service_provider_id = s.id WHERE a.agreement_type = 'ENTERPRISEPROMISE' AND a.valid_state = 'VALIDING' AND a.party_b = 'ENTERPRISEPEOPLE' AND s.id = #{param1}
AND a.agreement_no LIKE concat('%', #{param2}, '%')
AND e.enterprise_name LIKE concat('%', #{param3}, '%')

SELECT a.id AS agreement_id, 0 AS service_provider_id, e.enterprise_name, a.agreement_no, a.sign_type, a.agreement_type,
a.agreement_url, a.create_time, a.sign_state FROM diyi_agreement a INNER JOIN diyi_enterprise e ON a.party_b_id = e.id
WHERE a.agreement_type = 'ENTERPRISEPROMISE' AND a.valid_state = 'VALIDING' AND a.party_b = 'ENTERPRISEPEOPLE' AND e.id = #{param1}
