SELECT ep.id AS enterprise_provider_id, ep.enterprise_id, ep.service_provider_id, a.id AS agreement_id, a.agreement_no,a.paper_agreement_url, sp.service_provider_name, a.first_side_sign_person, a.online_agreement_url, a.sign_state, a.create_time FROM diyi_enterprise_service_provider ep INNER JOIN diyi_service_provider sp ON sp.id = ep.service_provider_id INNER JOIN diyi_agreement a ON sp.id = a.service_provider_id WHERE sp.is_deleted = 0 AND a.is_deleted = 0 AND ep.is_deleted = 0 and ep.enterprise_id =#{param1}
and sp.service_provider_name like concat('%', #{param2}, '%')
and a.agreement_no like concat('%', #{param3}, '%')
and a.sign_type=#{param4} AND a.agreement_type =#{param5}

SELECT ep.id AS enterprise_provider_id, ep.enterprise_id, ep.service_provider_id, a.id AS agreement_id, a.agreement_no,a.paper_agreement_url, sp.service_provider_name, a.first_side_sign_person, a.online_agreement_url, a.sign_state, a.create_time FROM diyi_enterprise_service_provider ep INNER JOIN diyi_service_provider sp ON sp.id = ep.service_provider_id INNER JOIN diyi_agreement a ON sp.id = a.service_provider_id WHERE sp.is_deleted = 0 AND a.is_deleted = 0 AND ep.is_deleted = 0 and ep.enterprise_id =#{param1}
and sp.service_provider_name like concat('%', #{param2}, '%')
and a.agreement_no like concat('%', #{param3}, '%')
and a.sign_type in ('PLATFORMAGREEMENT','PAPERAGREEMENT') AND a.agreement_type =#{param4}

SELECT a.id AS agreement_id, e.enterprise_name, e.id AS enterprise_id, a.agreement_no, m. NAME, a.sign_type, a.paper_agreement_url, a.online_agreement_url,
a.sign_state, a.create_time FROM diyi_agreement a INNER JOIN diyi_enterprise e ON a.enterprise_id = e.id LEFT JOIN diyi_maker m ON m.id = a.maker_id
AND m.is_deleted = 0 WHERE a.is_deleted = 0 AND e.is_deleted = 0 AND a.agreement_type = 'ENTMAKSUPPLEMENTARYAGREEMENT' AND a.enterprise_id = #{param1}

SELECT a.id AS agreement_id, s.id AS service_provider_id, a.agreement_no, a.sign_type, a.agreement_type, s.service_provider_name, a.online_agreement_url, a.paper_agreement_url, a.create_time, a.sign_state FROM diyi_agreement a INNER JOIN diyi_service_provider s ON a.service_provider_id = s.id WHERE a.agreement_type = 'SERVICEPROVIDERJOINAGREEMENT'
and a.agreement_no like concat('%', #{param1}, '%')
and s.id = #{param2}

SELECT a.id AS agreement_id,a.agreement_no, me.maker_id, me.enterprise_id, m. NAME AS maker_name, e.enterprise_name, a.online_agreement_url,
        a.paper_agreement_url, a.create_time FROM diyi_agreement a INNER JOIN diyi_maker_enterprise me ON me.enterprise_id = a.enterprise_id
        INNER JOIN diyi_maker m ON m.id = me.maker_id INNER JOIN diyi_enterprise e ON e.id = me.enterprise_id
        WHERE a.agreement_type = 'ENTMAKSUPPLEMENTARYAGREEMENT' and a.sign_state = 'SIGNED'

            and m.id = #{param1}


            and me.enterprise_id = #{param2}

SELECT a.id AS agreement_id, s.id AS service_provider_id,e.enterprise_name, a.agreement_no, a.sign_type, a.agreement_type, s.service_provider_name,
a.online_agreement_url, a.paper_agreement_url, a.create_time, a.sign_state FROM diyi_agreement a INNER JOIN diyi_enterprise e
ON a.enterprise_id = e.id INNER JOIN diyi_enterprise_service_provider ep ON ep.enterprise_id = e.id INNER JOIN diyi_service_provider s
ON ep.service_provider_id = s.id WHERE a.agreement_type = 'SERENTSUPPLEMENTARYAGREEMENT'

    AND e.id = #{param1}


    AND s.id = #{param2}
