SELECT ep.id AS enterprise_provider_id, ep.enterprise_id, ep.service_provider_id, a.id AS agreement_id, a.agreement_no, sp.service_provider_name, a.first_side_sign_person, a.online_aggrement_url, a.sign_state, a.sign_date FROM diyi_enterprise_provider ep INNER JOIN diyi_service_provider sp ON sp.id = ep.service_provider_id INNER JOIN diyi_agreement a ON sp.id = a.service_provider_id WHERE
 sp.is_deleted = 0 AND a.is_deleted = 0 AND ep.is_deleted = 0  and ep.enterprise_id =#{param1}

    and sp.service_provider_name like concat('%', #{param2}, '%')


    and a.agreement_no like concat('%', #{param3}, '%')

and a.sign_type=#{param4} AND a.agreement_type =#{param5}

SELECT ep.id AS enterprise_provider_id, ep.enterprise_id, ep.service_provider_id, a.id AS agreement_id, a.agreement_no, sp.service_provider_name, a.first_side_sign_person, a.online_aggrement_url, a.sign_state, a.sign_date FROM diyi_enterprise_provider ep INNER JOIN diyi_service_provider sp ON sp.id = ep.service_provider_id INNER JOIN diyi_agreement a ON sp.id = a.service_provider_id WHERE
sp.is_deleted = 0 AND a.is_deleted = 0 AND ep.is_deleted = 0  and ep.enterprise_id =#{param1}

    and sp.service_provider_name like concat('%', #{param2}, '%')


    and a.agreement_no like concat('%', #{param3}, '%')

and a.sign_type in ('PLATFORMAGREEMENT','PAPERAGREEMENT') AND a.agreement_type =#{param4}
