mapper.select:
SELECT t1.id, t1.name, t1.idcard_no, t1.phone_number, t1.bank_card_no, IF(t1.bank_card_verify_status = 'VERIFYPASS', TRUE, FALSE) AS bool_real_name_verify, IF((SELECT COUNT(id) FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t1.id AND sign_state = 'SIGNED' AND agreement_type = 1) > 0 AND (SELECT COUNT(id) FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t1.id AND sign_state = 'SIGNED' AND agreement_type = 9) > 0, TRUE, FALSE) AS bool_sign, IF((SELECT COUNT(id) FROM diyi_individual_business WHERE is_deleted = 0 AND maker_id = t1.id AND ibstate = 'OPERATING') > 0, TRUE, FALSE) AS bool_individual_business, IF((SELECT COUNT(id) FROM diyi_individual_enterprise WHERE is_deleted = 0 AND maker_id = t1.id AND ibstate = 'OPERATING') > 0, TRUE, FALSE) AS bool_individual_enterprise FROM diyi_maker t1
INNER JOIN diyi_maker_enterprise t2 ON t1.id = t2.maker_id AND t2.is_deleted = 0
WHERE t1.is_deleted = 0
AND t2.enterprise_id = #{param1}
AND t2.relationship_type = #{param2}
AND t1.certification_state = #{param3}
AND (t1.id like concat('%', #{param4}, '%') OR t1.name like concat('%', #{param4}, '%') OR t1.phone_number like concat('%', #{param4}, '%'))

SELECT pm.maker_net_income check_money, pm.total_fee income, e.enterprise_name, pm.create_time AS check_date FROM diyi_maker_enterprise me
INNER JOIN diyi_pay_enterprise pe ON me.enterprise_id = pe.enterprise_id INNER JOIN diyi_enterprise e ON e.id = me.enterprise_id INNER JOIN diyi_pay_maker pm
ON pm.pay_enterprise_id = pe.id WHERE pe.pay_state = 'CONFIRMPAY' pm.maker_id =#{param1} AND me.enterprise_id =#{param2}

SELECT e.enterprise_name, shid.charge_money_num AS income, shid.create_time AS check_date, shid.charge_money_num AS check_money FROM diyi_maker_enterprise me
INNER JOIN diyi_self_help_invoice shi ON me.enterprise_id = shi.enterprise_id INNER JOIN diyi_self_help_invoice_detail shid
ON shid.self_help_invoice_id = shi.id INNER JOIN diyi_enterprise e ON e.id = me.enterprise_id WHERE 1 = 1 AND me.maker_id =#{param1}
AND me.enterprise_id =#{param2}
