mapper.select:
SELECT
DISTINCT
t1.id, t1.name, t1.idcard_no, t1.phone_number, t1.bank_card_no, IF(t1.bank_card_verify_status = 'VERIFYPASS', TRUE, FALSE) AS bool_real_name_verify, IF((SELECT COUNT(id) FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t1.id AND sign_state = 'SIGNED' AND audit_state = 'APPROVED' AND agreement_type = 'MAKERJOINAGREEMENT') > 0 AND (SELECT COUNT(id) FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t1.id AND sign_state = 'SIGNED' AND audit_state = 'APPROVED' AND agreement_type = 'MAKERPOWERATTORNEY') > 0, TRUE, FALSE) AS bool_sign, IF((SELECT COUNT(id) FROM diyi_individual_business WHERE is_deleted = 0 AND maker_id = t1.id AND ibstate = 'OPERATING') > 0, TRUE, FALSE) AS bool_individual_business, IF((SELECT COUNT(id) FROM diyi_individual_enterprise WHERE is_deleted = 0 AND maker_id = t1.id AND ibstate = 'OPERATING') > 0, TRUE, FALSE) AS bool_individual_enterprise FROM diyi_maker t1
INNER JOIN diyi_maker_enterprise t2 ON t1.id = t2.maker_id AND t2.is_deleted = 0
INNER JOIN diyi_service_provider_maker t3 ON t1.id = t3.maker_id AND t3.is_deleted = 0
WHERE t1.is_deleted = 0
AND t2.enterprise_id = #{param1}
AND t3.service_provider_id = #{param2}
AND t2.relationship_type = #{param3}
AND t1.certification_state = #{param4}
AND (t1.idcard_no LIKE concat('%', #{param5}, '%') OR t1.name LIKE concat('%', #{param5}, '%') OR t1.phone_number LIKE concat('%', #{param5}, '%'))
