mapper.select:
SELECT t5.id, t5.avatar, t5.name, t5.certification_state, t5.self_desc, t5.idcard_no, t5.phone_number, t5.bank_card_no, t5.bank_name, t5.create_time, t5.maker_state, (SELECT COUNT(t2.id) FROM diyi_worksheet t1 INNER JOIN diyi_worksheet_maker t2 ON t1.id = t2.worksheet_id WHERE t1.is_deleted = 0 AND t2.is_deleted = 0 AND t2.worksheet_maker_state = 'VALIDATION'
AND t1.enterprise_id = #{param1}
AND t2.maker_id = t5.id) AS order_num, (SELECT IFNULL(SUM(t4.check_money), 0) FROM diyi_worksheet t3 INNER JOIN diyi_worksheet_maker t4 ON t3.id = t4.worksheet_id WHERE t3.is_deleted = 0 AND t4.is_deleted = 0 AND t4.worksheet_maker_state = 'VALIDATION'
AND t3.enterprise_id = #{param1}
AND t4.maker_id = t5.id) AS income, IF(t5.bank_card_verify_status = 'VERIFYPASS', TRUE, FALSE) AS bool_real_name_verify, (SELECT CASE sign_type WHEN 'PAPERAGREEMENT' THEN paper_agreement_url WHEN 'PLATFORMAGREEMENT' THEN online_aggrement_url WHEN 'THIRDAGREEMENT' THEN third_online_agreement_url WHEN 'UNILATERALPOWER' THEN paper_agreement_url WHEN 'ELEUNILATERALPOWER' THEN online_aggrement_url ELSE NULL END FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t5.id AND sign_state = 'SIGNED' AND agreement_type = 1) AS platform_join_contract, (SELECT CASE sign_type WHEN 'PAPERAGREEMENT' THEN paper_agreement_url WHEN 'PLATFORMAGREEMENT' THEN online_aggrement_url WHEN 'THIRDAGREEMENT' THEN third_online_agreement_url WHEN 'UNILATERALPOWER' THEN paper_agreement_url WHEN 'ELEUNILATERALPOWER' THEN online_aggrement_url ELSE NULL END FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t5.id AND sign_state = 'SIGNED' AND agreement_type = 10) AS enterprise_business_agreement, (SELECT CASE sign_type WHEN 'PAPERAGREEMENT' THEN paper_agreement_url WHEN 'PLATFORMAGREEMENT' THEN online_aggrement_url WHEN 'THIRDAGREEMENT' THEN third_online_agreement_url WHEN 'UNILATERALPOWER' THEN paper_agreement_url WHEN 'ELEUNILATERALPOWER' THEN online_aggrement_url ELSE NULL END FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t5.id AND sign_state = 'SIGNED' AND agreement_type = 9) AS license_agreement, (SELECT CASE sign_type WHEN 'PAPERAGREEMENT' THEN paper_agreement_url WHEN 'PLATFORMAGREEMENT' THEN online_aggrement_url WHEN 'THIRDAGREEMENT' THEN third_online_agreement_url WHEN 'UNILATERALPOWER' THEN paper_agreement_url WHEN 'ELEUNILATERALPOWER' THEN online_aggrement_url ELSE NULL END FROM diyi_agreement WHERE is_deleted = 0 AND maker_id = t5.id AND sign_state = 'SIGNED' AND agreement_type = 12) AS separate_agreement FROM diyi_maker t5 WHERE t5.is_deleted = 0 AND t5.id = #{param2}
